    const changebackground = () =>{
        if(window.scrollY >=50){
            setnav(true)
        }else{
            setnav(false)
        }
    }
    window.addEventListener('scroll', changebackground);
    return (
<>
   <nav className={nav ? 'nav active': 'nav'}>
